$(".slider").slick({
  slidesToShow: 1,
  autoplay: !0,
  autoplaySpeed: 5e3,
  arrows: !1,
  fade: !0,
  pauseOnFocus: !1,
  pauseOnHover: !1,
  cssEase: "ease-in-out",
  speed: 700,
  adaptiveHeight: !0
}),
  $(".down").click(function () {
    var e = $(this).attr("href");
    return $("html, body").animate({ scrollTop: $(e).offset().top + "px" }), !1;
  });
const burger = document.querySelector(".mobile-burger"),
  menu = document.querySelector(".menu"),
  logo = document.querySelector(".nav-header__logo");
burger.addEventListener("click", (e) => {
  burger.closest(".open")
    ? (burger.classList.remove("open"),
      menu.classList.remove("open"),
      (logo.style.display = ""))
    : (burger.classList.add("open"),
      menu.classList.add("open"),
      (logo.style.display = "none"));
}),
  $(".parallax-window").parallax({
  bleed: 1,
  imageSrc: "../img/form-bg.webp" || "../img/form-bg.jpg"
});
  AOS.init();
const image = document.querySelector(".support__img-1");
new simpleParallax(image, { orientation: "left", scale: 1.2 });
const image2 = document.querySelector(".support__img-2");
new simpleParallax(image2, { orientation: "right", scale: 1.2, delay: 2 });
const image3 = document.querySelector(".support__img-3");
new simpleParallax(image3, { orientation: "left", scale: 1.3 });
const modalForm = document.querySelector(".modal-form"),
  modalClose = document.querySelector(".modal-close"),
  sliderItemBtn = document.querySelectorAll(".slider-item__btn"),
  sliderItem = document.querySelectorAll(".slider__item"),
  header = document.querySelector(".header"),
  arrowDown = document.querySelector(".down"),
  modalFormBlock = document.querySelector(".modal-form-block");
sliderItemBtn.forEach((e) => {
  e.addEventListener("click", (e) => {
    e.preventDefault(),
      (modalForm.style.display = "block"),
      (modalFormBlock.style.transform = "translateX(0)"),
      (header.style.display = "none"),
      (arrowDown.style.display = "none"),
      (document.body.style.overflow = "hidden"),
      sliderItem.forEach((e) => {
        e.style.filter = "blur(10px)";
      });
  });
}),
  modalForm.addEventListener("click", (e) => {
    const o = e.target;
    (o.closest(".modal-form-block") && !o.closest(".modal-close")) ||
      ((modalForm.style.display = "none"),
      (modalFormBlock.style.transform = "translateX(-500px)"),
      (header.style.display = ""),
      (arrowDown.style.display = ""),
      (document.body.style.overflow = "auto"),
      sliderItem.forEach((e) => {
        e.style.filter = "";
      }));
  }),
  window.addEventListener("resize", () => {
    const e = document.querySelectorAll(".about__block");
    document.documentElement.clientWidth < 375 &&
      e.forEach((e) => {
        (e.dataset.aosDelay = 200), console.log(e.dataset.aosDelay);
      });
  });
